content.sql:
DECLARE @totalAmount DECIMAL(18,8) SET @totalAmount=0;
WITH sum_amount AS ( SELECT sum(gp.gm_amt) AS amt FROM GROSS_PROFIT gp WHERE substring(CONVERT(VARCHAR(10), gp.BILL_DATE, 120 ),1,7) = '$YearMon$' $orgId$ $distId$ $distCode$ $prodBrandId$ $prodName$ $prodCode$ ) SELECT @totalAmount=amt FROM sum_amount;
SELECT PN2,PN4,PN5,DIST_CODE, DIST_NAME, PROD_TYPE, PROD_CODE, PROD_NAME, QTY, AMT, gm_amt, CAST(CAST(gpm AS DECIMAL(38,3)) AS VARCHAR)+'%', con_margin FROM (
SELECT gp.PN2 , gp.PN4, gp.PN5, gp.DIST_CODE, gp.DIST_NAME, gp.PROD_TYPE, gp.PROD_CODE, gp.PROD_NAME, sum(gp.QTY) AS qty, sum(gp.AMT) AS amt, sum(gp.gm_amt) AS gm_amt, CASE WHEN sum(gp.amt) = 0 THEN 0 ELSE sum(gp.gm_amt)/sum(gp.amt)*100 END AS gpm, CASE WHEN @totalAmount = 0 THEN '0.000%' ELSE CAST(cast(sum(gp.gm_amt)/@totalAmount*100 as DECIMAL(38,3)) AS VARCHAR)+'%' END AS con_margin
FROM GROSS_PROFIT gp WHERE substring(CONVERT(VARCHAR(10), gp.BILL_DATE, 120 ),1,7) = '$YearMon$' $orgId$ $distId$ $distCode$ $prodBrandId$ $prodName$ $prodCode$ GROUP BY gp.PN2 , gp.PN4, gp.PN5, gp.DIST_CODE, gp.DIST_NAME, gp.PROD_TYPE, gp.PROD_CODE, gp.PROD_NAME
) a WHERE (a.gpm>15 OR a.gpm<-5)
UNION ALL
SELECT '合计：' AS PN2,'' AS PN4,'' AS PN5,'' AS DIST_CODE,'' AS DIST_NAME,'' AS PROD_TYPE,'' AS PROD_CODE,'' AS PROD_NAME, sum(gp.QTY) AS QTY, sum(gp.AMT) AS AMT, sum(gp.gm_amt) AS gm_amt, CASE WHEN sum(gp.amt) = 0 THEN '0.000%' ELSE CAST(CAST(sum(gp.gm_amt)/sum(gp.amt)*100 AS DECIMAL(38,3)) AS VARCHAR)+'%' END AS gpm,
'' AS con_margin
FROM GROSS_PROFIT gp WHERE substring(CONVERT(VARCHAR(10), gp.BILL_DATE, 120 ),1,7) = '$YearMon$' $orgId$ $distId$ $distCode$ $prodBrandId$ $prodName$ $prodCode$
